import * as BlogState from '../blog-state';
import * as blog from '../../store/reducers/blog.reducer';

export interface AppState {
  blog: any;
  authState: auth.State;
  BlogState: BlogState.BlogState;
}
export const reducers = {
    auth: auth1.reducer,
    blog: blog.blogReducer
};

import { IBlog } from './blog';
export interface BlogState {
  blog: IBlog[] | null;
  // error message
  Message: string | null;
}

export interface IBlog{
    title?: string;
    imageUrl?: string;
    description?: string;
    category?: string;
    date?: Date | null;
    id?: number | null;
}

export interface IUser{
    userName?: string;
    email?: any;
    phoneNo?: number;
    password?: any;
    accessToken?: any;
}

import { IUser } from '../models/user';
export interface State {
  // is a user authenticated?
  isAuthenticated: boolean;
  // if authenticated, there should be a user object
  user: IUser | null;
  // error message
  errorMessage: string | null;
}

export interface Theme {
  name: string;
  properties: any;
}

export const light: Theme = {
  name: 'light',
  properties: {
    '--background-default': '#F4FAFF',
    '--primary-default': '#000000',
    '--popup-button': '#0cfdba',
    '--popup-button-color': '#000000',
  }
};

export const dark: Theme = {
  name: 'dark',
  properties: {
    '--background-default': '#797C80',
    '--primary-default': '#ffffff',
    '--primary-button' : '#046147',
    '--popup-button-color': '#ffffff',
  }
};

